data.command:
console.warn("args :", args);
const tokenA = args[0].targets[0];
const tokenB = await fromUuid(args[0].tokenUuid);
console.warn("tokenA", tokenA);
console.warn("tokenB", tokenB);

const lastArg = args[args.length - 1];

let acBonus = new Roll(`1d8`).evaluate({ async: false });
game.dice3d?.showForRoll(acBonus);
console.warn("roll: ", acBonus.total);

const effect = {
    embedded: {
        ActiveEffect:{ 
            "Evasive Footwork":{
            icon : 'https://vtt.s3.eu-central-1.wasabisys.com/all/images/icons/own/evasion.svg',
            changes: [{
                "key": "data.attributes.ac.value",
                "mode": CONST.ACTIVE_EFFECT_MODES.ADD,
                "value": acBonus.total,
                }],
            duration : {rounds: 999},
            label: 'Evasive Footwork'
            }
        }
    }
}
await warpgate.mutate(tokenB, effect);
